mapper.select:
  	SELECT COUNT(*) CNT
  	FROM NOTICE_LIST
  	WHERE NOTICE_STATUS = 'Y'

			AND 








					NOTICE_TITLE LIKE #{val}


					NOTICE_CONTENT LIKE #{val}


					( NOTICE_TITLE LIKE #{val} OR NOTICE_CONTENT LIKE #{val} )


					MEMBER_ID LIKE #{val}

SELECT NOTICE_NO, NOTICE_TITLE, NOTICE_CONTENT, NOTICE_STATUS, CREATE_DT, MEMBER_NO, MEMBER_ID FROM NOTICE JOIN MEMBER USING(MEMBER_NO) WHERE NOTICE_STATUS = 'Y'
AND
NOTICE_TITLE LIKE #{val}
NOTICE_CONTENT LIKE #{val}
( NOTICE_TITLE LIKE #{val} OR NOTICE_CONTENT LIKE #{val} )
MEMBER_ID LIKE #{val}
ORDER BY NOTICE_NO DESC

mapper.update:
UPDATE NOTICE SET
NOTICE_STATUS = 'N'
WHERE NOTICE_NO = #{noticeNo}
