mapper.select:
SELECT COUNT(*) CNT FROM QUSETIONS_LIST WHERE QUSETIONS_STATUS = 'Y'
AND
QUSETIONS_TITLE LIKE #{val}
QUSETIONS_CONTENT LIKE #{val}
( QUSETIONS_TITLE LIKE #{val} OR QUSETIONS_CONTENT LIKE #{val} )

SELECT QUSETIONS_NO, QUSETIONS_TITLE, QUSETIONS_CONTENT, QUSETIONS_DT,MEMBER_NO, MEMBER_ID FROM QUSETIONS JOIN MEMBER USING(MEMBER_NO) WHERE QUSETIONS_STATUS = 'Y'
AND
QUSETIONS_TITLE LIKE #{val}
QUSETIONS_CONTENT LIKE #{val}
( QUSETIONS_TITLE LIKE #{val} OR QUSETIONS_CONTENT LIKE #{val} )
ORDER BY QUSETIONS_NO DESC

mapper.update:
UPDATE QUSETIONS SET
QUSETIONS_STATUS = 'N'
WHERE QUSETIONS_NO =#{qusetionsNo}
